INSERT IGNORE INTO `mail_template` (`code`, `subject`, `body_html`, `body_text`) VALUES
('password_reset', 'Réinitialisation de votre mot de passe', '<p>Bonjour {username},</p>\r\n<p>Vous avez demandé une réinitialisation de votre mot de passe. Veuillez cliquer sur le lien ci-dessous pour continuer :</p>\r\n<p><a href=\"{link}\">Réinitialiser mon mot de passe</a></p>\r\n<p>Si vous n\'êtes pas à l\'origine de cette demande, veuillez ignorer cet email.</p>\r\n<p>Ce lien expirera dans une heure.</p>', 'Bonjour {username},\r\nVous avez demandé une réinitialisation de votre mot de passe. Veuillez copier-coller le lien suivant dans votre navigateur pour continuer :\r\n{link}\r\n\r\nSi vous n\'êtes pas à l\'origine de cette demande, veuillez ignorer cet email.\r\n\r\nCe lien expirera dans une heure.'),
('password_modified', 'Votre mot de passe a été modifié', '<p>Bonjour {username},</p>\r\n<p>Votre mot de passe a été modifié.</p>\r\n<p>Si vous n\'êtes pas à l\'origine de cette modification, veuillez aller vous connecter en cliquant sur mot de passe oublié à l\'aide de cette adresse mail.</p>\r\n<p>Si cela est impossible ou ne fonctionne pas, veuillez nous contacter rapidement à {email_club}</p>', 'Bonjour {username},\r\nVotre mot de passe a été modifié.\r\nSi vous n\'êtes pas à l\'origine de cette modification, veuillez aller vous connecter en cliquant sur mot de passe oublié à l\'aide de cette adresse mail.\r\nSi cela est impossible ou ne fonctionne pas, veuillez nous contacter rapidement à {email_club}.'),
('new_account', 'Votre compte {app_name}', '<p>Bonjour {username},</p><p>Votre compte {app_name} a été créé. Veuillez cliquer sur le lien ci-dessous pour saisir le mot de passe associé :</p><p><a href=\"{link}\">Créer mon mot de passe</a></p><p>Ce lien expirera dans {timeout_token_new_account}.</p>', 'Bonjour {username},\r\nVotre compte {app_name} a été créé. Veuillez cliquer sur le lien ci-dessous pour saisir le mot de passe associé :\r\n\r\n<a href=\"{link}\">Créer mon mot de passe</a>\r\nCe lien expirera dans {timeout_token_new_account}.'),
('paiement_confirme', 'Confirmation de commande', '<p>Bonjour {prenom},</p><p>Nous confirmons avoir bien reçu votre paiement pour la réservation ci-dessous.</p><p>Vous pouvez accéder aux informations de votre commande en <a href=\"{UrlModifData}/modifData?token={token}\">cliquant ici</a></p><p>Pour rappel :&nbsp;</p><p>Numéro d\'enregistrement : <strong>{IDreservation}</strong><br>Date et lieu du gala : <i><u>{EventLibelle}</u></i>, début <strong>{DateEvent}</strong> à la piscine <i>{Piscine}</i><br>Nom : {ReservationNomPrenom}<br>Mail : {Reservationmail}<br>Tel : {Reservationtel}<br>Nombre total de place(s) : {ReservationNbTotalPlace}<br><br>Détail des places demandées :&nbsp;<br>{AffichRecapDetailPlaces}<br><br>Nous vous rappelons que les places choisies sont susceptibles d\'être décalée d\'au moins 1 place sur la même rangée pour des raisons d\'organisation.</p><p><br><strong>{TotalAPayer} {ReservationMontantTotal}</strong><br><br><br>{SIGNATURE}</p>', 'Bonjour {prenom},\r\n\r\nNous confirmons avoir bien reçu votre paiement pour la réservation ci-dessous.\r\n\r\nVous pouvez accéder aux informations de votre commande en <a href=\"{URLPATH}/modifData?token={token}\">cliquant ici</a>\r\n\r\nPour rappel :\r\n\r\n\r\n    Numéro d\'enregistrement : {IDreservation}\r\n    Date et lieu du gala : {EventLibelle}, début {DateEvent} à la piscine {Piscine}\r\n    Nom : {ReservationNomPrenom}\r\n    Mail : {Reservationmail}\r\n    Tel : {Reservationtel}\r\n    Nombre total de place(s) : {ReservationNbTotalPlace}\r\n    \r\n    Détail des places demandées :\r\n    {AffichRecapDetailPlaces}\r\n    \r\n    Nous vous rappelons que les places choisies sont susceptibles d\'être décalée d\'au moins 1 place sur la même rangée pour des raisons d\'organisation.\r\n\r\n\r\n\r\n    {TotalAPayer} {ReservationMontantTotal}\r\n\r\n\r\n    {SIGNATURE}'),
('paiement_confirme_add', 'Confirmation de complément de commande', '<p>Bonjour {prenom},</p><p>Nous confirmons avoir bien reçu votre paiement pour le complément de la réservation ci-dessous.</p><p>Vous pouvez toujours accéder aux informations de votre commande en <a href=\"{UrlModifData}/modifData?token={token}\">cliquant ici</a></p><p>Pour rappel :&nbsp;</p><p>Numéro d\'enregistrement : <strong>{IDreservation}</strong><br>Date et lieu du gala : <i><u>{EventLibelle}</u></i>, début <strong>{DateEvent}</strong> à la piscine <i>{Piscine}</i><br>Nom : {ReservationNomPrenom}<br>Mail : {Reservationmail}<br>Tel : {Reservationtel}<br>Nombre total de place(s) : {ReservationNbTotalPlace}<br><br>Détail des places demandées :&nbsp;<br>{AffichRecapDetailPlaces}<br><br>Nous vous rappelons que les places choisies sont susceptibles d\'être décalée d\'au moins 1 place sur la même rangée pour des raisons d\'organisation.</p><p><br><strong>{TotalAPayer} {ReservationMontantTotal}</strong><br><br><br>{SIGNATURE}</p>', 'Bonjour {prenom},\r\n\r\nNous confirmons avoir bien reçu votre paiement pour le complément de la réservation ci-dessous.\r\n\r\nVous pouvez toujours accéder aux informations de votre commande en <a href=\"{URLPATH}/modifData?token={token}\">cliquant ici</a>\r\n\r\nPour rappel :\r\n\r\n\r\n    Numéro d\'enregistrement : {IDreservation}\r\n    Date et lieu du gala : {EventLibelle}, début {DateEvent} à la piscine {Piscine}\r\n    Nom : {ReservationNomPrenom}\r\n    Mail : {Reservationmail}\r\n    Tel : {Reservationtel}\r\n    Nombre total de place(s) : {ReservationNbTotalPlace}\r\n    \r\n    Détail des places demandées :\r\n    {AffichRecapDetailPlaces}\r\n    \r\n    Nous vous rappelons que les places choisies sont susceptibles d\'être décalée d\'au moins 1 place sur la même rangée pour des raisons d\'organisation.\r\n\r\n\r\n\r\n    {TotalAPayer} {ReservationMontantTotal}\r\n\r\n\r\n    {SIGNATURE}');
